func authHeaders(token string) map[string]string {
	if token == "" {
		return map[string]string{"Authorization": ""}
	}
	return map[string]string{"Authorization": "Bearer " + token}
}

func getIden(pos int) authid.Identity {
	switch pos {
	case 0:
		return defaultIdentity
	case 1:
		return secondIdentity
	case 2:
		return thirdIdentity
	case 3:
		return fourthIdentity
	case 4:
		return fifthIdentity
	case 5:
		return sixthIdentity
	case 6:
		return seventhIdentity
	case 7:
		return eighthIdentity
	case 8:
		return ninthIdentity
	case 9:
		return tenthIdentity
	case 10:
		return eleventhIdentity
	default:
		panic(fmt.Sprintf("getIden underfined pos %d", pos))
	}
}

